:root {
  --font-size: 16px;
  --font-size-title: 15px;
  --font-size-hints: 11px;
  --font-size-url: 13px;

  --font-weight: 400;
  --font-weight-medium: 500;
  --font-weight-bold: 600;

  --font: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", sans-serif;

  --padding: 3px;
  --padding-vomnibar: 8px 0;

  /* Modern, softer shadows */
  --shadow: 0px 8px 32px rgba(0, 0, 0, 0.08), 0px 2px 8px rgba(0, 0, 0, 0.04);
  --blur: 20px;

  /* Foregrounds - refined contrast */
  --fg: #18181b;
  --fg-dark: #71717a;
  --fg-match: #09090b;
  --fg-url: #a1a1aa;

  /* Backgrounds - pure white base */
  --bg: #ffffff;
  --bg-selected: rgba(63, 63, 70, 0.06);
  --bg-hover: rgba(63, 63, 70, 0.04);

  /* Borders - subtle */
  --border: #e4e4e7;
  --border-focus: #d4d4d8;

  /* Hints - accent color */
  --fg-hints: #0a0a0a;
  --bg-hints: #ffffff;
  --accent: #3b82f6;
}

/* Vomnibar */

#vomnibar {
  background: var(--bg);
  border: 1px solid var(--border);
  box-shadow: var(--shadow);
  border-radius: 12px;
  overflow: hidden;
  left: 50%;
  margin-left: -400px;
  max-width: 800px;
  backdrop-filter: blur(40px);
}

#vomnibar #vomnibar-search-area,
#vomnibar input {
  background: transparent;
  border: none;
  box-shadow: none;
  color: var(--fg);
  font-family: var(--font);
  font-size: var(--font-size);
  font-weight: var(--font-weight);
  letter-spacing: -0.01em;
}

#vomnibar #vomnibar-search-area {
  padding: 16px 52px;
  border-radius: 12px 12px 0 0;
  transition: background-color 0.15s ease;
}

#vomnibar #vomnibar-search-area:focus-within {
  background: var(--bg-hover);
}

#vomnibar input {
  border: none;
  padding: var(--padding);
  outline: none;
}

#vomnibar input::placeholder {
  color: var(--fg-dark);
  opacity: 0.6;
}

#vomnibar #vomnibar-search-area::before {
  content: "";
  position: absolute;
  top: 19px;
  left: 18px;
  background-image: url("data:image/svg+xml;utf8,<svg viewBox='0 0 24 24' fill='none' xmlns='http://www.w3.org/2000/svg'><path d='M14.9536 14.9458L21 21M17 10C17 13.866 13.866 17 10 17C6.13401 17 3 13.866 3 10C3 6.13401 6.13401 3 10 3C13.866 3 17 6.13401 17 10Z' stroke='%23a1a1aa' stroke-width='2' stroke-linecap='round' stroke-linejoin='round'/></svg>");
  background-repeat: no-repeat;
  background-size: contain;
  width: 20px;
  height: 20px;
  opacity: 0.7;
}

.no-insert-text {
  display: none;
}

#vomnibar ul {
  background: transparent;
  border-top: 1px solid var(--border);
  margin: 0 12px;
  margin-bottom: 8px;
  padding: var(--padding-vomnibar);
  list-style: none;
}

#vomnibar li {
  border-bottom: none;
  padding: 12px 14px;
  border-radius: 8px;
  transition: all 0.12s ease;
  cursor: pointer;
}

#vomnibar li:hover {
  background-color: var(--bg-hover);
}

#vomnibar li .top-half {
  padding: var(--padding);
  display: flex;
  align-items: center;
  gap: 6px;
}

#vomnibar li .bottom-half {
  padding: 2px 0px 0px 3px;
  margin: 0;
  margin-top: 4px;
}

#vomnibar li .source {
  color: var(--fg-dark);
  font-family: var(--font);
  font-size: 12px;
  font-weight: var(--font-weight-medium);
  text-transform: uppercase;
  letter-spacing: 0.04em;
  opacity: 0.7;
}

#vomnibar li em,
#vomnibar li .title {
  color: var(--fg);
  font-family: var(--font);
  font-size: var(--font-size-title);
  font-weight: var(--font-weight-medium);
  letter-spacing: -0.01em;
  line-height: 1.4;
}

#vomnibar li .url {
  color: var(--fg-url);
  font-family: var(--font);
  font-size: var(--font-size-url);
  font-weight: var(--font-weight);
  letter-spacing: -0.005em;
  opacity: 0.8;
}

#vomnibar li .match {
  color: var(--fg-match);
  font-weight: var(--font-weight-bold);
}

#vomnibar li .title .match {
  color: var(--accent);
  font-weight: var(--font-weight-bold);
  background: rgba(59, 130, 246, 0.1);
  padding: 1px 3px;
  border-radius: 3px;
}

#vomnibar li.selected {
  background-color: var(--bg-selected);
  border-radius: 8px;
  transform: scale(0.995);
}

#vomnibar li.selected .url {
  color: var(--fg-url);
  opacity: 1;
}

#vomnibar li.selected .match {
  color: var(--accent);
}

/* Hints Marks */

#vimium-hint-marker-container div.internal-vimium-hint-marker,
#vimium-hint-marker-container div.vimiumHintMarker {
  background: var(--bg-hints);
  border: 1px solid var(--border);
  box-shadow: 0px 4px 16px rgba(0, 0, 0, 0.08), 0px 1px 4px rgba(0, 0, 0, 0.04);
  padding: 4px 6px;
  border-radius: 6px;
  backdrop-filter: blur(10px);
}

#vimium-hint-marker-container div span {
  color: var(--fg-hints);
  font-family: var(--font);
  font-size: var(--font-size-hints);
  font-weight: var(--font-weight-bold);
  text-shadow: none;
  letter-spacing: 0.02em;
}

#vimium-hint-marker-container div > .matchingCharacter {
  opacity: 0.25;
}

#vimium-hint-marker-container div > .matchingCharacter ~ span {
  color: var(--accent);
  font-weight: 700;
}

/* HUD */

#hud-container {
  background: var(--bg);
  border: 1px solid var(--border);
  box-shadow: var(--shadow);
  border-radius: 10px;
  backdrop-filter: blur(20px);
}

#hud-container span#hud-find-input,
#hud-container #hud {
  color: var(--fg);
  font-family: var(--font);
  font-size: 14px;
  font-weight: var(--font-weight);
  letter-spacing: -0.01em;
}

#hud-container .hud-find {
  background-color: transparent;
  border: none;
}

#hud-container #search-area {
  background-color: transparent;
  padding: 8px 12px;
}
